import React from 'react';
import Button from './Button';
import { useState } from 'react';

const PageSelector = ({onGetTable}) => {

    const [wikiUrl, setWikiUrl] = useState('');

    const onSubmit = (e) => { 
        e.preventDefault();
        if(!text)
        {
            alert('Please add a wikipedia URL');
        }

        onGetTable({wikiUrl});};

    const onRandomTable = () => { 

        // Get some random URL

        // put it in textbox 
        setWikiUrl('https://en.wikipedia.org/wiki/Women%27s_high_jump_world_record_progression');
        onGetTable({wikiUrl});
    };

    return (
        <div>
            <form className='add-form' onSubmit={onSubmit}>
                <div className='form-control'>
                    <input
                        type='text'
                        placeholder='Wikipedia article URL'
                        value={wikiUrl}
                        onChange={(e) => setWikiUrl(e.target.value)}
                    />                   
                </div>
                <input type='submit' value='Get table' className='btn btn-block' />

                {/*  <Button color='green' text='Get table' onClick={onGetTable}/> */}
                
            </form>

            <Button text='Random table' onClick={onRandomTable} />
        </div>
    )
}

export default PageSelector
